//
// Provides some keying material when playing with tokens in samples
// using IdentityModel: https://github.com/AzureAD/azure-activedirectory-identitymodel-extensions-for-dotnet
//

using System;
using System.Security.Cryptography.X509Certificates;
using Microsoft.IdentityModel.Tokens;

namespace IdentityModelSampleUtilities
{
    public static class KeyMaterial
    {
        // password: SelfSigned2048_SHA256
        private static string _selfSigned2048_SHA256 = @"";
        private static string _selfSigned2048_SHA256_Public = @"";

        public static X509Certificate2 CertificateWithPrivateKey {  get => new X509Certificate2(Convert.FromBase64String(_selfSigned2048_SHA256), "SelfSigned2048_SHA256"); }

        public static X509Certificate2 CertificateWithPublicKey { get => new X509Certificate2(Convert.FromBase64String(_selfSigned2048_SHA256_Public)); }

        public static X509SecurityKey X509SecurityKeyWithPrivateKey { get => new X509SecurityKey(CertificateWithPrivateKey); }

        public static X509SecurityKey X509SecurityKeyWithPublicKey { get => new X509SecurityKey(CertificateWithPublicKey); }
    }
}
